//using System;class P{static void Main(string[]args){var n=Console.ReadLine().Split(' ');int e=int.Parse(n[3]),f=int.Parse(n[4]),g=int.Parse(n[7]);var h=new int[g+1];for(int i=0;i<g;i++){n=Console.ReadLine().Split(' ');int k=int.Parse(n[0]),l=int.Parse(n[1]);h[k]=l;}while(true){n=Console.ReadLine().Split(' ');g=int.Parse(n[0]);int m=int.Parse(n[1]);var o=n[2];Console.WriteLine(o=="NONE"?"WAIT":o=="LEFT"&&((g==e?f:h[g])>m)||o=="RIGHT"&&((g==e?f:h[g])<m)?"BLOCK":"WAIT");}}}